import React, { useState, useRef } from "react";
import { useWebSocket } from "../hooks/useWebSocket";
import "./ChatBox.css";

const ChatBox = ({ transcript, setTranscript, setResponseAudio }) => {
    const [isListening, setIsListening] = useState(false);
    const audioRecorderRef = useRef(null);

    const { sendAudio } = useWebSocket("https://fantastic-funicular-rp9vpwrw5pp25pp4-8000.app.github.dev//api/v1/chat/");

    const handleStartListening = () => {
        const constraints = { audio: true };

        navigator.mediaDevices.getUserMedia(constraints)
            .then((stream) => {
                const mediaRecorder = new MediaRecorder(stream);
                mediaRecorder.ondataavailable = (event) => {
                    const audioBlob = event.data;
                    console.log("Recorded Audio Blob:", audioBlob);
                    // Save or process the audioBlob here
                };
                mediaRecorder.start();
            })
            .catch((error) => {
                console.error("Error accessing microphone:", error);
            });
    };

    const handleStopListening = async () => {
        setIsListening(false);

        // Replace this with the logic to capture audio data from your recorder
        const audioBlob = new Blob(); // Example placeholder
        console.log("AudioBlob:", audioBlob);

        try {
            const serverResponse = await sendAudio(audioBlob);

            if (serverResponse.error) {
                console.error("Server Error:", serverResponse.error);
            } else {
                setTranscript((prev) => [...prev, serverResponse.user_text]);
                setResponseAudio(serverResponse.audio);
            }
        } catch (error) {
            console.error("Error sending audio:", error.message);
        }
    };

    return (
        <div className="chat-box">
            <button onClick={isListening ? handleStopListening : handleStartListening}>
                {isListening ? "Stop Listening" : "Start Listening"}
            </button>
        </div>
    );
};

export default ChatBox;
